const express = require('express')
const app = express()

app.set('view engine','ejs')

app.use(express.json())
app.use(express.urlencoded({extended:true}))

app.get('/',(req,res)=>{
    res.render('index')
})

app.post('/',(req,res)=>{
    var num=Number(req.body.number)

    if(num%2===0){
        res.send('even')
    }else{
        res.send('odd')
    }
})
app.listen(3000,()=>{
    console.log('server started....');
});